CREATE DATABASE blog;
USE blog;

CREATE TABLE users (
   id INT AUTO_INCREMENT,
   username VARCHAR(60),
   password VARCHAR(60),
   PRIMARY KEY (id)
);

CREATE TABLE articles (
   id INT AUTO_INCREMENT,
   title VARCHAR(60),
   author VARCHAR(60),
   summary VARCHAR(10000),
   review MEDIUMTEXT,
   image VARCHAR(255),
   PRIMARY KEY (id)
);

